/**
 * LocationContext.jsx
 * Last updated: 2025-04-08
 * 
 * Context provider for location-related features including:
 * - User locations
 * - User paths
 * - Users along the current user's path
 */

import React, { createContext, useState, useEffect, useContext, useRef } from 'react';
import api from '../utils/api';
import { AuthContext } from './AuthContext';

export const LocationContext = createContext();

export const LocationProvider = ({ children }) => {
  const { user } = useContext(AuthContext);
  const [position, setPosition] = useState(null);
  const [liveUsers, setLiveUsers] = useState([]);
  const [livePaths, setLivePaths] = useState([]);
  const [usersAlongPath, setUsersAlongPath] = useState([]); // New state for users along path
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);
  const [lastUpdated, setLastUpdated] = useState(new Date());
  const [showIntersectingOnly, setShowIntersectingOnly] = useState(false);
  const [proximityRadius, setProximityRadius] = useState(500); // Default 500m radius

  // Ref to prevent concurrent requests and track timestamps
  const fetchTimers = useRef({
    users: 0,
    paths: 0,
    usersAlongPath: 0
  });

  // Flag to track if requests are in progress
  const requestInProgress = useRef({
    users: false,
    paths: false,
    usersAlongPath: false
  });

  // Update the user's location
  const updateLocation = async (latitude, longitude) => {
    if (!user) return;

    try {
      console.log(`[2025-04-08 14:51:24] Updating location for user ${user.username || 'itsanubhav009'}: ${latitude}, ${longitude}`);
      const response = await api.post('/location/update', { latitude, longitude });
      setPosition({ latitude, longitude });
      setLastUpdated(new Date());
      return response.data;
    } catch (error) {
      console.error('Error updating location:', error);
      setError('Failed to update location');
      throw error;
    }
  };

  // Create a path between source and destination
  const createPath = async (source, destination) => {
    if (!user) return;

    try {
      console.log(`[2025-04-08 14:51:24] User ${user.username || 'itsanubhav009'} creating path from [${source.lat}, ${source.lng}] to [${destination.lat}, ${destination.lng}]`);

      const response = await api.post('/path/set', {
        source,
        destination
      });

      // Refresh paths after creating a new one
      await fetchLivePaths(true); // Force refresh

      // Also fetch users along the new path
      await fetchUsersAlongPath(true);

      setLastUpdated(new Date());

      return response.data;
    } catch (error) {
      if (error.response && error.response.status === 403) {
        console.warn('User is not active enough to create paths');
        setError('You must have an active location to create paths');
      } else {
        console.error('Error creating path:', error);
        setError('Failed to create path: ' + (error.response?.data?.message || error.message));
      }
      throw error;
    }
  };

  // Update proximity radius for user filtering
  const updateProximityRadius = (radius) => {
    console.log(`[2025-04-08 14:51:24] Updating proximity radius to ${radius}m`);
    setProximityRadius(radius);

    // Refresh users along path with new radius
    fetchUsersAlongPath(true);
  };

  // Toggle intersection filtering
  const toggleIntersectionFilter = () => {
    const newValue = !showIntersectingOnly;
    setShowIntersectingOnly(newValue);

    // Refresh paths with the new filter setting
    fetchLivePaths(true, newValue); // Force refresh with new filter

    return newValue;
  };

  // Fetch paths data with aggressive throttling
  // Fix the fetchLivePaths function - REMOVE the /api prefix
const fetchLivePaths = async (force = false, intersectOnly = showIntersectingOnly) => {
  // Rest of the function remains the same

  try {
    requestInProgress.current.paths = true;
    setIsLoading(true);
    console.log(`[2025-04-08 15:05:58] Fetching paths with intersection filter: ${intersectOnly}`);

    // FIX: Remove the /api prefix, it's already in your base URL config
    const response = await api.get(`/path/live?intersectOnly=${intersectOnly}`);

    console.log(`[2025-04-08 15:05:58] Fetched ${response.data.data?.length || 0} paths`);
    fetchTimers.current.paths = now;
    setLivePaths(response.data.data || []);
    setLastUpdated(new Date());
    return response.data.data;
  } catch (error) {
    // Rest of the function remains the same
  }
};

// Fix the fetchUsersAlongPath function - REMOVE the /api prefix
const fetchUsersAlongPath = async (force = false) => {
  // Rest of the function remains the same

  try {
    requestInProgress.current.usersAlongPath = true;
    setIsLoading(true);
    console.log(`[2025-04-08 15:05:58] Fetching users along current path with radius: ${proximityRadius}m`);

    // FIX: Remove the /api prefix, it's already in your base URL config
    const response = await api.get(`/location/along-my-path?radius=${proximityRadius}`);

    console.log(`[2025-04-08 15:05:58] Found ${response.data.data?.length || 0} users along the path`);
    fetchTimers.current.usersAlongPath = now;
    setUsersAlongPath(response.data.data || []);
    setLastUpdated(new Date());
    return response.data.data;
  } catch (error) {
    // Rest of the function remains the same
  }
};
  // Fetch all active users' locations with aggressive throttling
  const fetchLiveUsers = async (force = false) => {
    // If a request is already in progress, skip this one
    if (requestInProgress.current.users && !force) {
      console.log('User request already in progress, skipping');
      return liveUsers;
    }

    const now = Date.now();
    // Only fetch if forced or it's been at least 60 seconds since last check
    if (!force && now - fetchTimers.current.users < 60000) {
      console.log(`Skipping fetchLiveUsers - too soon (${Math.round((now - fetchTimers.current.users)/1000)}s)`);
      return liveUsers;
    }

    try {
      requestInProgress.current.users = true;
      setIsLoading(true);
      console.log('[2025-04-08 14:51:24] Fetching live users...');

      const response = await api.get('/location/live');
      fetchTimers.current.users = now;

      // Process and validate the data before setting state
      const userData = response.data.data || [];
      console.log(`[2025-04-08 14:51:24] Got ${userData.length} users from API`);

      // Validate coordinates before setting state
      const validUsers = userData.filter(u => {
        const lat = parseFloat(u.latitude);
        const lng = parseFloat(u.longitude);
        const isValid = !isNaN(lat) && !isNaN(lng) && Math.abs(lat) <= 90 && Math.abs(lng) <= 180;

        if (!isValid && u) {
          console.warn(`Filtered out user with invalid coordinates:`, u.username, u.latitude, u.longitude);
        }

        return isValid;
      });

      console.log(`[2025-04-08 14:51:24] ${validUsers.length} users have valid coordinates`);
      setLiveUsers(validUsers);
      setLastUpdated(new Date());
      return validUsers;
    } catch (error) {
      console.error('Error fetching users:', error);
      setError('Failed to fetch users');
      return liveUsers;
    } finally {
      setIsLoading(false);
      requestInProgress.current.users = false;
    }
  };

  // Get browser geolocation - only do this at startup
  const getCurrentPosition = () => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          const { latitude, longitude } = position.coords;
          setPosition({ latitude, longitude });

          // Optionally update server with this location
          if (user) {
            updateLocation(latitude, longitude)
              .catch(err => console.error('Failed to update initial location:', err));
          }
        },
        (error) => {
          console.error('Geolocation error:', error);
          setError('Failed to get location');
        }
      );
    } else {
      setError('Geolocation is not supported by this browser');
    }
  };

  // Force refresh data function - useful for manual refresh button
  const forceRefreshData = async () => {
    console.log("[2025-04-08 14:51:24] Force refreshing location data...");
    setIsLoading(true);
    try {
      // Bypass throttling
      fetchTimers.current = {
        users: 0,
        paths: 0,
        usersAlongPath: 0
      };

      // First get users
      const userData = await fetchLiveUsers(true);
      console.log("[2025-04-08 14:51:24] Users refreshed:", userData?.length || 0);

      // Then get paths
      const pathsData = await fetchLivePaths(true);
      console.log("[2025-04-08 14:51:24] Paths refreshed:", pathsData?.length || 0);

      // Then get users along path
      const usersAlongPathData = await fetchUsersAlongPath(true);
      console.log("[2025-04-08 14:51:24] Users along path refreshed:", usersAlongPathData?.length || 0);

      setLastUpdated(new Date());
      return { 
        users: userData, 
        paths: pathsData,
        usersAlongPath: usersAlongPathData
      };
    } catch (error) {
      console.error("Error in force refresh:", error);
      setError("Failed to refresh location data: " + error.message);
      throw error;
    } finally {
      setIsLoading(false);
    }
  };

  // Initial setup and much less frequent polling
  useEffect(() => {
    if (user) {
      // Get current location first
      getCurrentPosition();

      // Do initial data load
      const initialLoad = async () => {
        console.log('[2025-04-08 14:51:24] Performing initial data load for user', user.username || 'itsanubhav009');
        try {
          await fetchLiveUsers(true);
          await fetchLivePaths(true);
          await fetchUsersAlongPath(true);
        } catch (error) {
          console.error('Error in initial data load:', error);
        }
      };

      initialLoad();

      // Set up polling with MUCH LONGER interval (2 minutes instead of 30 seconds)
      const intervalId = setInterval(() => {
        console.log('[2025-04-08 14:51:24] Performing periodic data refresh');
        fetchLiveUsers().catch(err => console.error('Failed to fetch users:', err));
        fetchLivePaths().catch(err => console.error('Failed to fetch paths:', err));
        fetchUsersAlongPath().catch(err => console.error('Failed to fetch users along path:', err));
      }, 120000); // 120 seconds (2 minutes)

      return () => clearInterval(intervalId);
    }
  }, [user]);

  // Effect to refetch users along path when radius changes
  useEffect(() => {
    if (user && fetchTimers.current.usersAlongPath > 0) { // Only if not first load
      console.log(`[2025-04-08 14:51:24] Proximity radius changed to ${proximityRadius}m, refreshing users along path`);
      fetchUsersAlongPath(true).catch(err => console.error('Failed to fetch users along path after radius change:', err));
    }
  }, [proximityRadius, user]);

  return (
    <LocationContext.Provider
      value={{
        position,
        liveUsers,
        livePaths,
        usersAlongPath,
        isLoading,
        error,
        lastUpdated,
        showIntersectingOnly,
        proximityRadius,
        updateLocation,
        createPath,
        fetchLiveUsers,
        fetchLivePaths,
        fetchUsersAlongPath,
        toggleIntersectionFilter,
        updateProximityRadius,
        getCurrentPosition,
        forceRefreshData
      }}
    >
      {children}
    </LocationContext.Provider>
  );
};

export default LocationProvider;
