import React, { createContext, useState, useEffect, useContext, useRef } from 'react';
import api from '../utils/api';
import { AuthContext } from './AuthContext';

export const LocationContext = createContext();

export const LocationProvider = ({ children }) => {
  const { user } = useContext(AuthContext);
  const [position, setPosition] = useState(null);
  const [liveUsers, setLiveUsers] = useState([]);
  const [livePaths, setLivePaths] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);
  const [lastUpdated, setLastUpdated] = useState(new Date());
  const [showIntersectingOnly, setShowIntersectingOnly] = useState(false);

  // Add these refs to track fetch timing
  const fetchTimers = useRef({
    users: 0,
    paths: 0
  });

  // Update the user's location
  const updateLocation = async (latitude, longitude) => {
    if (!user) return;

    try {
      const response = await api.post('/location/update', { latitude, longitude });
      setPosition({ latitude, longitude });
      setLastUpdated(new Date());
      return response.data;
    } catch (error) {
      console.error('Error updating location:', error);
      setError('Failed to update location');
      throw error;
    }
  };

  // Create a path between source and destination
  const createPath = async (source, destination) => {
    if (!user) return;

    try {
      console.log('Creating path between:', source, destination);

      const response = await api.post('/path/set', {
        source,
        destination
      });

      // Refresh paths after creating a new one
      await fetchLivePaths();
      setLastUpdated(new Date());

      return response.data;
    } catch (error) {
      if (error.response && error.response.status === 403) {
        console.warn('User is not active enough to create paths');
        setError('You must have an active location to create paths');
      } else {
        console.error('Error creating path:', error);
        setError('Failed to create path: ' + (error.response?.data?.message || error.message));
      }
      throw error;
    }
  };

  // Toggle intersection filtering
  const toggleIntersectionFilter = () => {
    const newValue = !showIntersectingOnly;
    setShowIntersectingOnly(newValue);

    // Refresh paths with the new filter setting
    fetchLivePaths();

    return newValue;
  };

  // Fetch paths data for online users
 // This goes in your LocationContext.jsx file

const fetchLivePaths = async (intersectOnly = false) => {
  setIsLoading(true);
  try {
    console.log('Fetching live paths...');
    const response = await axios.get(`/api/path/live?intersectOnly=${intersectOnly}`);

    if (!response.data) {
      console.error('No data returned from path API');
      return;
    }

    // Log the structure of what we received
    console.log('Path API response structure:', {
      hasCurrentUserPath: !!response.data.currentUser?.path,
      intersectingUsersCount: response.data.intersectingUsers?.length || 0,
      dataArrayLength: response.data.data?.length || 0
    });

    // Use the combined data array for compatibility
    let paths = [];

    if (Array.isArray(response.data.data)) {
      paths = response.data.data;
    } else if (response.data.currentUser?.path || (response.data.intersectingUsers?.length > 0)) {
      // Manually combine data from structured format
      paths = [
        ...(response.data.currentUser?.path ? [response.data.currentUser.path] : []),
        ...(response.data.intersectingUsers || [])
      ];
    }

    // Ensure we include paths from both current user and others
    console.log(`Fetched ${paths.length} total paths`);

    if (paths.length > 0) {
      // Log each path to help with debugging
      paths.forEach((path, index) => {
        console.log(`Path ${index + 1}:`, {
          id: path.id,
          user_id: path.user_id,
          username: path.username || 'Unknown',
          isCurrentUser: path.user_id === user?.id,
          hasRoute: !!path.route
        });
      });

      setLivePaths(paths);
    } else {
      console.log('No paths found');
      setLivePaths([]);
    }
  } catch (error) {
    console.error('Error fetching live paths:', error);
    setLivePaths([]);
  } finally {
    setIsLoading(false);
  }
};

  // Fetch all active users' locations
  const fetchLiveUsers = async () => {
    // Prevent calling too frequently
    const now = Date.now();
    if (now - fetchTimers.current.users < 20000) {
      console.log(`Skipping fetchLiveUsers - too soon (${Math.round((now - fetchTimers.current.users)/1000)}s)`);
      return liveUsers;
    }

    setIsLoading(true);
    try {
      console.log(`Fetching live users at ${new Date().toISOString()}`);
      const response = await api.get('/location/live');

      fetchTimers.current.users = now;
      setLiveUsers(response.data.data || []);
      setLastUpdated(new Date());

      console.log(`Fetched ${response.data.data?.length || 0} active users`);
      return response.data.data;
    } catch (error) {
      console.error('Error fetching users:', error);
      setError('Failed to fetch users');
    } finally {
      setIsLoading(false);
    }
  };

  // Get browser geolocation
  const getCurrentPosition = () => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          const { latitude, longitude } = position.coords;
          setPosition({ latitude, longitude });

          // Optionally update server with this location
          if (user) {
            updateLocation(latitude, longitude)
              .catch(err => console.error('Failed to update initial location:', err));
          }
        },
        (error) => {
          console.error('Geolocation error:', error);
          setError('Failed to get location');
        }
      );
    } else {
      setError('Geolocation is not supported by this browser');
    }
  };

  // Get current location and set up polling when component mounts
  useEffect(() => {
    if (user) {
      getCurrentPosition();

      // Set up polling for live data
      const intervalId = setInterval(() => {
        fetchLiveUsers().catch(err => console.error('Failed to fetch users:', err));
        fetchLivePaths().catch(err => console.error('Failed to fetch paths:', err));
      }, 10000); // Use 30-second interval

      return () => clearInterval(intervalId);
    }
  }, [user]);

  return (
    <LocationContext.Provider
      value={{
        position,
        liveUsers,
        livePaths,
        isLoading,
        error,
        lastUpdated,
        showIntersectingOnly,
        updateLocation,
        createPath,
        fetchLiveUsers,
        fetchLivePaths,
        toggleIntersectionFilter,
        getCurrentPosition
      }}
    >
      {children}
    </LocationContext.Provider>
  );
};
