import React, { useEffect, useState, useContext, useRef } from 'react';
import { MapContainer, TileLayer, Marker, Popup, Polyline, useMap, useMapEvents } from 'react-leaflet';
import { LocationContext } from '../context/LocationContext';
import { AuthContext } from '../context/AuthContext';
import { ChatContext } from '../context/ChatContext';
import ChatDialog from './ChatDialog';
import NotificationsPanel from './NotificationsPanel';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';
import './UserLocationMap.css';
import NotificationButton from './NotificationButton';
// Fix for default icons
import icon from 'leaflet/dist/images/marker-icon.png';
import iconShadow from 'leaflet/dist/images/marker-shadow.png';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faComments } from '@fortawesome/free-solid-svg-icons';

// Debug component to display path information
function DebugInfo() {
  const { livePaths } = useContext(LocationContext);
  const { user } = useContext(AuthContext);

  return (
    <div style={{ 
      position: 'absolute', 
      bottom: '10px', 
      left: '10px', 
      backgroundColor: 'rgba(255,255,255,0.8)', 
      padding: '10px',
      zIndex: 1000,
      maxWidth: '300px',
      maxHeight: '200px',
      overflow: 'auto',
      fontSize: '12px',
      border: '1px solid #ccc'
    }}>
      <h4>Debug Info</h4>
      <div>Raw Paths: {livePaths?.length || 0}</div>
      <div>Current User: {user?.id || 'Unknown'}</div>
      {livePaths && livePaths.length > 0 && (
        <div>
          <h5>First Path:</h5>
          <div>ID: {livePaths[0].id}</div>
          <div>User: {livePaths[0].user_id === user?.id ? 'Current User' : 'Other'}</div>
          <div>Has Route: {livePaths[0].route ? 'Yes' : 'No'}</div>
          <div>Route Start: {livePaths[0].route?.substring(0, 30)}...</div>
        </div>
      )}
    </div>
  );
}

let DefaultIcon = L.icon({
  iconUrl: icon,
  shadowUrl: iconShadow,
  iconSize: [25, 41],
  iconAnchor: [12, 41]
});

L.Marker.prototype.options.icon = DefaultIcon;

// Create custom icons
const createCustomIcon = (color) => {
  return L.divIcon({
    className: 'custom-div-icon',
    html: `<div class="marker-pin" style="background-color: ${color};"></div>`,
    iconSize: [30, 42],
    iconAnchor: [15, 42]
  });
};

// Enhanced parseRouteData function with extensive debugging
const parseRouteData = (routeData) => {
  console.log(`[PARSE] Processing route data of type ${typeof routeData}`);

  if (!routeData) {
    console.log('[PARSE] Route data is null or undefined');
    return [];
  }

  // LINESTRING parsing
  if (typeof routeData === 'string' && routeData.startsWith('LINESTRING')) {
    try {
      console.log(`[PARSE] Parsing LINESTRING: ${routeData.substring(0, 50)}...`);

      // Strict WKT LINESTRING parsing
      // Format is: LINESTRING(lon1 lat1, lon2 lat2, ...)
      const lineString = routeData.trim();

      // Extract content within parentheses
      const coordsText = lineString.substring(
        lineString.indexOf('(') + 1, 
        lineString.lastIndexOf(')')
      );

      // Split by commas to get each point
      const pointStrings = coordsText.split(',');
      console.log(`[PARSE] Found ${pointStrings.length} point strings`);

      if (pointStrings.length === 0) {
        console.log('[PARSE] No points found in LINESTRING');
        return [];
      }

      // Parse each point string into [lat, lng] array
      const points = pointStrings.map(pointStr => {
        // Trim and split by space
        const parts = pointStr.trim().split(' ');

        if (parts.length < 2) {
          console.log(`[PARSE] Invalid point format: ${pointStr}`);
          return null;
        }

        // WKT format is longitude first, latitude second
        // But Leaflet needs [lat, lng] format
        const lng = parseFloat(parts[0]);
        const lat = parseFloat(parts[1]);

        if (isNaN(lat) || isNaN(lng)) {
          console.log(`[PARSE] Invalid coordinates: ${pointStr}`);
          return null;
        }

        return [lat, lng]; // Swapped for Leaflet's [lat, lng] format
      }).filter(Boolean); // Remove any null points

      console.log(`[PARSE] Successfully parsed ${points.length} valid points`);

      // Validate first and last points
      if (points.length > 0) {
        const first = points[0];
        const last = points[points.length - 1];
        console.log(`[PARSE] First point: [${first[0]}, ${first[1]}]`);
        console.log(`[PARSE] Last point: [${last[0]}, ${last[1]}]`);

        // Reduce excessive points for very long routes (optional)
        let simplifiedPoints = points;
        if (points.length > 500) {
          console.log(`[PARSE] Simplifying path from ${points.length} to ~500 points`);
          const factor = Math.ceil(points.length / 500);
          simplifiedPoints = points.filter((_, i) => i % factor === 0 || i === points.length - 1);
          console.log(`[PARSE] Simplified to ${simplifiedPoints.length} points`);
        }

        return simplifiedPoints;
      }

      return [];
    } catch (e) {
      console.error('[PARSE] Error parsing LINESTRING:', e);
      console.error('[PARSE] Problematic route data:', routeData.substring(0, 100) + '...');
      return [];
    }
  }

  // If it's already an array, make sure it's in the right format
  if (Array.isArray(routeData)) {
    console.log(`[PARSE] Route data is already an array with ${routeData.length} elements`);
    return routeData;
  }

  console.log('[PARSE] Unrecognized route data format');
  return [];
};

// Component to update map bounds based on users' positions and routes
function MapBoundsUpdater({ users, paths }) {
  const map = useMap();

  useEffect(() => {
    if (!users.length && !paths.length) return;

    try {
      const points = [];

      // Add user points
      users.forEach(user => {
        if (user.latitude && user.longitude) {
          points.push([user.latitude, user.longitude]);
        }
      });

      // Add path points (with better handling for complex routes)
      paths.forEach(path => {
        if (path.parsedRoute && Array.isArray(path.parsedRoute)) {
          // Don't add every point - just add first, last, and some in between
          // to avoid overloading the bounds calculation with too many points
          const routePoints = path.parsedRoute;
          if (routePoints.length > 0) {
            points.push(routePoints[0]); // Add first point

            if (routePoints.length > 10) {
              // Add a few points in the middle for long routes
              points.push(routePoints[Math.floor(routePoints.length / 3)]);
              points.push(routePoints[Math.floor(routePoints.length * 2 / 3)]);
            }

            if (routePoints.length > 1) {
              points.push(routePoints[routePoints.length - 1]); // Add last point
            }
          }
        }
      });

      if (points.length > 0) {
        const bounds = L.latLngBounds(points);
        map.fitBounds(bounds, { padding: [50, 50] });
      }
    } catch (error) {
      console.error('Error updating map bounds:', error);
    }
  }, [users, paths, map]);

  return null;
}

// Component to handle map clicks for route creation
function MapClickHandler({ routeMode, selectedSource, setSelectedSource, createPath }) {
  useMapEvents({
    click: (e) => {
      if (!routeMode) return;

      const { lat, lng } = e.latlng;

      if (!selectedSource) {
        // Set source
        setSelectedSource({ lat, lng });
      } else {
        // Set destination and create path
        const destination = { lat, lng };
        createPath(selectedSource, destination);

        // Reset route mode (this will be handled by parent component)
      }
    }
  });

  return null;
}

const UserLocationMap = () => {
  const { liveUsers, fetchLiveUsers, livePaths, fetchLivePaths, createPath } = useContext(LocationContext);
  const { user } = useContext(AuthContext);
  // Get all the chat context values we need including activeChats
  const { 
    sendChatRequest, 
    notifications, 
    currentChat, 
    openChat,
    activeChats,
    fetchActiveChats
  } = useContext(ChatContext);

  const [usersWithLocation, setUsersWithLocation] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [lastUpdated, setLastUpdated] = useState(new Date());
  const [pathsWithCoordinates, setPathsWithCoordinates] = useState([]);
  const [routeMode, setRouteMode] = useState(false);
  const [selectedSource, setSelectedSource] = useState(null);
  const [showNotifications, setShowNotifications] = useState(false);
  const mapRef = useRef(null);

  // Chat request handler
  const handleChatRequest = (userId) => {
    if (userId === user.id) {
      // Don't allow chatting with yourself
      return;
    }

    sendChatRequest(userId)
      .then((response) => {
        console.log('Chat request sent successfully:', response);
      })
      .catch((error) => {
        console.error('Failed to send chat request:', error);
      });
  };

  // Fetch active chats on component mount
  useEffect(() => {
    if (user && fetchActiveChats) {
      fetchActiveChats();
    }
  }, [user, fetchActiveChats]);

  useEffect(() => {
    let isMounted = true;

    const loadMapData = async () => {
      if (!isMounted || isLoading) return;

      console.log(`Map data refresh at ${new Date().toLocaleTimeString()}`);

      try {
        // Only update if too much time has passed since last update
        const timeSinceUpdate = new Date() - lastUpdated;
        if (timeSinceUpdate > 25000) { // 25 seconds minimum
          setIsLoading(true);
          await fetchLiveUsers();
          await fetchLivePaths();
          setLastUpdated(new Date());
        } else {
          console.log(`Skipping refresh - only ${Math.round(timeSinceUpdate/1000)}s since last update`);
        }
      } catch (error) {
        console.error('Error refreshing map data:', error);
      } finally {
        if (isMounted) {
          setIsLoading(false);
        }
      }
    };

    // Initial load
    loadMapData();

    // Set up a controlled interval with a single timer
    // Use 30 seconds to reduce server load
    const intervalId = setInterval(loadMapData, 30000);

    return () => {
      isMounted = false;
      clearInterval(intervalId);
    };
  }, [fetchLiveUsers, fetchLivePaths, lastUpdated, isLoading]);

  useEffect(() => {
    if (liveUsers) {
      const filtered = liveUsers.filter(u => u.latitude && u.longitude);
      setUsersWithLocation(filtered);
    }
  }, [liveUsers]);

  // Updated effect for path processing
  useEffect(() => {
    console.log('[MAP] Processing livePaths update, count:', livePaths?.length || 0);

    if (!livePaths || !Array.isArray(livePaths) || livePaths.length === 0) {
      console.log('[MAP] No paths to process, clearing display');
      setPathsWithCoordinates([]);
      return;
    }

    try {
      // Process each path
      const processed = livePaths.map((path, idx) => {
        console.log(`[MAP] Processing path ${idx}:`, { 
          id: path.id,
          user_id: path.user_id,
          isCurrentUser: path.user_id === user?.id
        });

        // Check for route data
        let routeData = path.route;
        if (!routeData) {
          // Try alternate property names
          if (path.route_wkt) {
            console.log(`[MAP] Path ${idx} has route_wkt instead of route`);
            routeData = path.route_wkt;
          } else {
            console.log(`[MAP] Path ${idx} has no route data`);
            return null;
          }
        }

        // Parse and validate the route
        const parsedRoute = parseRouteData(routeData);

        if (!parsedRoute || parsedRoute.length === 0) {
          console.log(`[MAP] Path ${idx} returned no valid coordinates`);
          return null;
        }

        console.log(`[MAP] Path ${idx} processed successfully with ${parsedRoute.length} points`);

        // Return the processed path
        return {
          ...path,
          parsedRoute,
          isCurrentUser: path.user_id === user?.id
        };
      }).filter(Boolean); // Remove null paths

      console.log(`[MAP] Processed ${processed.length} paths with valid coordinates`);

      if (processed.length === 0 && livePaths.length > 0) {
        console.warn('[MAP] WARNING: All paths failed to process!');
      }

      setPathsWithCoordinates(processed);
    } catch (error) {
      console.error('[MAP] Error processing paths:', error);
      setPathsWithCoordinates([]);
    }
  }, [livePaths, user]);

  // Updated createPath handler
  const handleCreatePath = async (source, destination) => {
    try {
      console.log('[CREATE] Creating path from', source, 'to', destination);
      setIsLoading(true);

      // Create the path
      const result = await createPath(source, destination);

      console.log('[CREATE] Path created successfully:', result);

      // Force a fresh load of path data
      console.log('[CREATE] Refreshing paths after creation...');
      await fetchLivePaths(false);

      // Update UI state
      setLastUpdated(new Date());
      setRouteMode(false);
      setSelectedSource(null);

      // Alert the user
      alert('Path created successfully! If it doesn\'t appear, click the Refresh Map button.');
    } catch (error) {
      console.error('[CREATE] Error creating path:', error);
      alert('Failed to create path: ' + (error.message || 'Unknown error'));
    } finally {
      setIsLoading(false);
    }
  };

  const toggleRouteMode = () => {
    setRouteMode(!routeMode);
    if (routeMode) {
      // If turning off, clear selected source
      setSelectedSource(null);
    }
  };

  const formatTime = (date) => {
    if (!(date instanceof Date)) {
      try {
        date = new Date(date);
      } catch (e) {
        return 'Unknown';
      }
    }
    return date.toLocaleTimeString();
  };

  if (isLoading && usersWithLocation.length === 0 && pathsWithCoordinates.length === 0) {
    return <div className="map-loading">Loading map...</div>;
  }

  // Calculate center point for map display or use a default
  let centerLat = 0, centerLng = 0;

  if (usersWithLocation.length > 0) {
    centerLat = usersWithLocation.reduce((sum, u) => sum + parseFloat(u.latitude || 0), 0) / usersWithLocation.length;
    centerLng = usersWithLocation.reduce((sum, u) => sum + parseFloat(u.longitude || 0), 0) / usersWithLocation.length;
  } else {
    // Default to a central location if no users
    centerLat = 0;
    centerLng = 0;
  }

  return (
    <div className="map-container">
      <div className="map-header">
        <h3>Live User Map</h3>
        <div className="map-controls">
          <button 
            className="refresh-button"
            onClick={async () => {
              console.log('[MAP] Manual refresh requested');
              setIsLoading(true);
              try {
                // Force immediate data refresh
                await fetchLiveUsers();
                await fetchLivePaths(false);
                setLastUpdated(new Date());

                // Log what we have
                console.log(`[MAP] Refresh complete - ${livePaths.length} paths available`);
              } catch (error) {
                console.error('[MAP] Error during refresh:', error);
              } finally {
                setIsLoading(false);
              }
            }}
            disabled={isLoading}
          >
            {isLoading ? 'Refreshing...' : 'ðŸ”„ Refresh Map'}
          </button>

          <div className="notification-controls">
            <NotificationButton 
              notificationCount={notifications ? notifications.length : 0}
              onClick={() => setShowNotifications(!showNotifications)}
            />

            {showNotifications && (
              <NotificationsPanel onClose={() => setShowNotifications(false)} />
            )}
          </div>

          <button 
            className={`route-button ${routeMode ? 'active' : ''}`} 
            onClick={toggleRouteMode}
          >
            {routeMode ? 'Cancel Route' : 'Create Route'}
          </button>

          {routeMode && !selectedSource && (
            <div className="route-instructions">Click on map to set starting point</div>
          )}

          {routeMode && selectedSource && (
            <div className="route-instructions">Click on map to set destination</div>
          )}

          <span className="last-updated">
            Last updated: {formatTime(lastUpdated)}
          </span>
        </div>
      </div>

      <MapContainer 
        center={[centerLat, centerLng]} 
        zoom={3} 
        style={{ height: '600px', width: '100%' }}
        ref={mapRef}
      >
        <TileLayer
          attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
        />

        {/* Add the Debug component */}
        <DebugInfo />

        {/* Map click handler for route creation */}
        <MapClickHandler 
          routeMode={routeMode} 
          selectedSource={selectedSource} 
          setSelectedSource={setSelectedSource} 
          createPath={handleCreatePath}
        />

        {/* Display user markers */}
        {usersWithLocation.map(u => (
          <Marker 
            key={`user-${u.id}`} 
            position={[parseFloat(u.latitude), parseFloat(u.longitude)]}
            icon={createCustomIcon(u.id === user?.id ? '#4285F4' : '#FF5722')}
          >
            <Popup>
              <div className="user-popup">
                <h3>{u.username}</h3>
                <div className="user-status">
                  <span className="status-dot"></span>
                  Online
                </div>
                <div className="user-location">
                  Coordinates: {parseFloat(u.latitude).toFixed(4)}, {parseFloat(u.longitude).toFixed(4)}
                </div>
                <div className="user-last-active">
                  Last active: {formatTime(u.last_active)}
                </div>
                {u.id === user?.id ? (
                  <div className="current-user-tag">This is you</div>
                ) : (
                  <div className="chat-button-container">
                    <button 
                      className="chat-request-button"
                      onClick={() => handleChatRequest(u.id)}
                    >
                      Chat with {u.username}
                    </button>
                  </div>
                )}
              </div>
            </Popup>
          </Marker>
        ))}

        {/* Display route source marker if in route mode */}
        {routeMode && selectedSource && (
          <Marker 
            position={[selectedSource.lat, selectedSource.lng]}
            icon={createCustomIcon('#00C853')}
          >
            <Popup>Starting point</Popup>
          </Marker>
        )}

        {/* Display paths as polylines */}
        {pathsWithCoordinates.map((path, idx) => {
          console.log(`[RENDER] Rendering path ${idx}:`, {
            id: path.id, 
            points: path.parsedRoute.length,
            isCurrentUser: path.user_id === user?.id
          });

          return (
            <Polyline 
              key={`path-${path.id || idx}`} 
              positions={path.parsedRoute} 
              color={path.user_id === user?.id ? '#2196F3' : '#FF5722'} 
              weight={5} // Increased line weight for better visibility
              opacity={0.8}
              // Add event listeners for debugging
              eventHandlers={{
                mouseover: () => {
                  console.log(`[RENDER] Path ${idx} mouseover:`, {
                    id: path.id,
                    user: path.user_id,
                    points: path.parsedRoute.length
                  });
                }
              }}
            >
              <Popup>
                <div className="path-popup">
                  <h4>Route Information</h4>
                  <p><strong>User:</strong> {path.user_id === user?.id ? 'You' : (path.username || 'Other User')}</p>
                  <p><strong>Created:</strong> {formatTime(path.created_at)}</p>
                  {path.parsedRoute.length > 0 && (
                    <>
                      <p><strong>From:</strong> {path.parsedRoute[0][0].toFixed(4)}, {path.parsedRoute[0][1].toFixed(4)}</p>
                      <p><strong>To:</strong> {path.parsedRoute[path.parsedRoute.length-1][0].toFixed(4)}, {path.parsedRoute[path.parsedRoute.length-1][1].toFixed(4)}</p>
                      <p><strong>Points:</strong> {path.parsedRoute.length}</p>
                    </>
                  )}
                </div>
              </Popup>
            </Polyline>
          );
        })}

        {/* Map bounds updater component */}
        <MapBoundsUpdater 
          users={usersWithLocation} 
          paths={pathsWithCoordinates} 
        />
      </MapContainer>

      <div className="map-legend">
        <h4>Map Legend</h4>
        <div className="legend-items">
          <div className="legend-item">
            <div className="legend-marker" style={{ backgroundColor: '#4285F4' }}></div>
            <span>Your location</span>
          </div>
          <div className="legend-item">
            <div className="legend-marker" style={{ backgroundColor: '#FF5722' }}></div>
            <span>Other users</span>
          </div>
          <div className="legend-item">
            <div className="legend-line" style={{ backgroundColor: '#2196F3' }}></div>
            <span>Your paths</span>
          </div>
          <div className="legend-item">
            <div className="legend-line" style={{ backgroundColor: '#FF5722' }}></div>
            <span>Other users' paths</span>
          </div>
          {routeMode && (
            <div className="legend-item">
              <div className="legend-marker" style={{ backgroundColor: '#00C853' }}></div>
              <span>Selected starting point</span>
            </div>
          )}
        </div>
      </div>

      {/* Display path information and active chats panel */}
      <div className="map-info-panel">
        <h4>Statistics</h4>
        <div className="map-stats">
          <div className="stat-item">
            <strong>Active Users:</strong> {usersWithLocation.length}
          </div>
          <div className="stat-item">
            <strong>Active Paths:</strong> {pathsWithCoordinates.length}
          </div>
          <div className="stat-item">
            <strong>Last Updated:</strong> {formatTime(lastUpdated)}
          </div>
        </div>

        {/* New Active Chats Panel */}
        <div className="active-chats">
          <h4>
            <FontAwesomeIcon icon={faComments} /> Active Chats
          </h4>
          <div className="chats-list">
            {activeChats && activeChats.length > 0 ? (
              activeChats.map(chat => (
                <div 
                  key={chat.id} 
                  className="chat-item"
                  onClick={() => openChat(chat.id)}
                >
                  <div className="chat-user">
                    {chat.otherParticipants && chat.otherParticipants.length > 0 
                      ? chat.otherParticipants[0].username 
                      : 'Unknown User'}
                  </div>
                  {chat.unreadCount > 0 && (
                    <span className="unread-badge">{chat.unreadCount}</span>
                  )}
                </div>
              ))
            ) : (
              <div className="no-chats">No active chats</div>
            )}
          </div>
        </div>

        {pathsWithCoordinates.length > 0 && (
          <div className="recent-paths">
            <h4>Recent Paths</h4>
            <div className="paths-list">
              {pathsWithCoordinates.slice(0, 5).map((path, idx) => (
                <div key={idx} className="path-item">
                  <div className="path-header">
                    <strong>{path.username || 'Unknown user'}</strong>
                    <span>{formatTime(path.created_at)}</span>
                  </div>
                  <div className="path-details">
                    {path.parsedRoute.length > 0 && (
                      <>
                        <small>
                          From: {path.parsedRoute[0][0].toFixed(4)}, {path.parsedRoute[0][1].toFixed(4)}
                        </small>
                        <small>
                          To: {path.parsedRoute[path.parsedRoute.length-1][0].toFixed(4)}, {path.parsedRoute[path.parsedRoute.length-1][1].toFixed(4)}
                        </small>
                        <small>
                          Route Points: {path.parsedRoute.length}
                        </small>
                      </>
                    )}
                  </div>
                </div>
              ))}
            </div>
          </div>
        )}
      </div>

      {/* Chat dialog */}
      {currentChat && <ChatDialog />}

      {/* CSS for refresh button */}
      <style jsx>{`
        .refresh-button {
          background-color: #4CAF50;
          color: white;
          border: none;
          padding: 8px 16px;
          margin: 0 8px;
          border-radius: 4px;
          cursor: pointer;
          font-weight: bold;
        }

        .refresh-button:hover {
          background-color: #45a049;
        }

        .refresh-button:disabled {
          background-color: #cccccc;
          cursor: not-allowed;
        }
      `}</style>
    </div>
  );
};

export default UserLocationMap;
