    try {
      // Bypass throttling
      fetchTimers.current = {
        users: 0,
        paths: 0,
        usersAlongPath: 0
      };

      // Reset data hashes to force UI updates
      dataCache.current = {
        usersHash: null,
        pathsHash: null,
        usersAlongPathHash: null
      };

      // First get users
      const userData = await fetchLiveUsers(true);
      console.log("[2025-04-09 10:55:36] Users refreshed:", userData?.length || 0);

      // Then get paths
      const pathsData = await fetchLivePaths(true);
      console.log("[2025-04-09 10:55:36] Paths refreshed:", pathsData?.length || 0);

      // Then get users along path
      const usersAlongPathData = await fetchUsersAlongPath(true);
      console.log("[2025-04-09 10:55:36] Users along path refreshed:", usersAlongPathData?.length || 0);

      setLastUpdated(new Date());
      return { 
        users: userData, 
        paths: pathsData,
        usersAlongPath: usersAlongPathData
      };
    } catch (error) {
      console.error("Error in force refresh:", error);
      setError("Failed to refresh location data: " + error.message);
      throw error;
    } finally {
      setIsLoading(false);
    }
  };

  // Initial setup 
  useEffect(() => {
    if (user) {
      // Get current location first
      getCurrentPosition();

      // Do initial data load
      const initialLoad = async () => {
        console.log('[2025-04-09 10:55:36] Performing initial data load for user', user.username || '[unknown]');
        if (!user.username) {
          console.error('[2025-04-09 11:25:30] Missing username for API operation');
          setError('Your profile information is incomplete. Please log out and log in again.');
          return;
        }
        try {
          await fetchLiveUsers(true);
          await fetchLivePaths(true);
          await fetchUsersAlongPath(true);
        } catch (error) {
          console.error('Error in initial data load:', error);
        }
      };

      initialLoad();

      // Set up high-frequency polling (2 seconds)
      const intervalId = setInterval(() => {
        // These request won't show loading indicators or trigger error states unless there's a significant change
        fetchLiveUsers().catch(err => {
          if (err.message !== 'canceled') console.error('Failed to fetch users:', err);
        });

        fetchLivePaths().catch(err => {
          if (err.message !== 'canceled') console.error('Failed to fetch paths:', err);
        });

        fetchUsersAlongPath().catch(err => {
          if (err.message !== 'canceled' && !(err.response && err.response.status === 404)) {
            console.error('Failed to fetch users along path:', err);
          }
        });
      }, 2000); // 2 seconds refresh interval

      return () => clearInterval(intervalId);
    }
  }, [user]);

  // Effect to refetch users along path when radius changes
  useEffect(() => {
    if (user && fetchTimers.current.usersAlongPath > 0) { // Only if not first load
      console.log(`[2025-04-09 10:55:36] Proximity radius changed to ${proximityRadius}m, refreshing users along path`);
      fetchUsersAlongPath(true).catch(err => console.error('Failed to fetch users along path after radius change:', err));
    }
  }, [proximityRadius, user]);

  return (
    <LocationContext.Provider
      value={{
        position,
        liveUsers,
        livePaths,
        usersAlongPath,
        isLoading,
        error,
        lastUpdated,
        showIntersectingOnly,
        proximityRadius,
        updateLocation,
        createPath,
        fetchLiveUsers,
        fetchLivePaths,
        fetchUsersAlongPath,
        toggleIntersectionFilter,
        updateProximityRadius,
        getCurrentPosition,
        forceRefreshData
      }}
    >
      {children}
    </LocationContext.Provider>
  );
};

export default LocationProvider;
