import React, { useState, useEffect, useContext, useRef } from 'react';
import { ChatContext } from '../context/ChatContext';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faTimes, faPaperPlane, faSpinner, faInfoCircle } from '@fortawesome/free-solid-svg-icons';
import './ChatDialog.css';

const ChatDialog = () => {
  const { 
    currentChat, 
    messages, 
    sendMessage, 
    closeChat, 
    fetchMessages,
    chatStatus,
    fetchActiveChats
  } = useContext(ChatContext);

  const [messageText, setMessageText] = useState('');
  const [sending, setSending] = useState(false);
  const [sendError, setSendError] = useState(null);
  const messagesEndRef = useRef(null);
  const pollingRef = useRef(null);

  // Scroll to bottom of messages
  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  // Effect to scroll to bottom when messages change
  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  // Effect to check if the chat is active
  useEffect(() => {
    if (currentChat) {
      // Initial check if the chat is active
      if (!currentChat.isActive) {
        console.log('Chat is not active. Setting up polling to check status...');

        // Start polling for chat status
        const pollChatStatus = async () => {
          try {
            const updatedChats = await fetchActiveChats(true);
            const updatedChat = updatedChats.find(c => c.id === currentChat.id);

            if (updatedChat && updatedChat.isActive) {
              console.log('Chat is now active!');
              // Stop polling once active
              clearInterval(pollingRef.current);
              pollingRef.current = null;

              // Refresh messages since the chat is now active
              fetchMessages(currentChat.id, true);
            }
          } catch (error) {
            console.error('Error polling chat status:', error);
          }
        };

        // Poll every 2 seconds
        pollingRef.current = setInterval(pollChatStatus, 2000);

        // Initial poll
        pollChatStatus();
      } else {
        // Chat is already active, start regular message polling
        const messagePollingId = setInterval(() => {
          fetchMessages(currentChat.id);
        }, 5000);

        return () => clearInterval(messagePollingId);
      }

      // Cleanup
      return () => {
        if (pollingRef.current) {
          clearInterval(pollingRef.current);
          pollingRef.current = null;
        }
      };
    }
  }, [currentChat, fetchActiveChats, fetchMessages]);

  const handleSendMessage = async (e) => {
    e.preventDefault();

    if (!messageText.trim() || !currentChat) return;

    try {
      setSending(true);
      setSendError(null);
      await sendMessage(currentChat.id, messageText);
      setMessageText('');
    } catch (error) {
      console.error('Failed to send message:', error);

      // Set specific error messages based on the error
      if (error.response?.data?.error_code === 'CHAT_INACTIVE') {
        setSendError('The chat is not active yet. Please wait for activation.');
      } else if (error.response?.data?.error_code === 'NOT_JOINED') {
        setSendError('You have not joined this chat. Please refresh and try again.');
      } else {
        setSendError('Failed to send message: ' + (error.response?.data?.message || error.message));
      }

      // Clear error after 5 seconds
      setTimeout(() => setSendError(null), 5000);
    } finally {
      setSending(false);
    }
  };

  if (!currentChat) return null;

  const otherUser = currentChat.otherParticipants && currentChat.otherParticipants[0];
  const isActive = currentChat.isActive;
  const hasJoined = currentChat.hasJoined;

  return (
    <div className="chat-dialog">
      <div className="chat-header">
        <div className="chat-title">
          {otherUser ? otherUser.username : 'Chat'}
          {!isActive && <span className="chat-status-pill">Pending</span>}
        </div>
        <button className="close-button" onClick={closeChat}>
          <FontAwesomeIcon icon={faTimes} />
        </button>
      </div>

      {(!isActive || !hasJoined) && (
        <div className="chat-pending-banner">
          <FontAwesomeIcon icon={faInfoCircle} />
          {!isActive 
            ? "Waiting for chat to become active. Both users need to accept the chat request."
            : "You need to join this chat before sending messages."}
        </div>
      )}

      <div className="chat-messages">
        {messages.length > 0 ? (
          messages.map((msg) => (
            <div 
              key={msg.id} 
              className={`message ${msg.user_id === currentChat.otherParticipants[0]?.id ? 'received' : 'sent'}`}
            >
              <div className="message-bubble">
                {msg.message}
              </div>
              <div className="message-info">
                {msg.profiles?.username || 'User'} Â· {new Date(msg.created_at).toLocaleTimeString([], {hour: '2-digit', minute:'2-digit'})}
              </div>
            </div>
          ))
        ) : (
          <div className="no-messages">
            {isActive && hasJoined
              ? "No messages yet. Start the conversation!"
              : "Waiting for chat to become active. Messages will appear here once both users have joined."}
          </div>
        )}
        <div ref={messagesEndRef} />
      </div>

      {chatStatus && (
        <div className={`chat-dialog-status ${chatStatus.type}`}>
          {chatStatus.message}
        </div>
      )}

      {sendError && (
        <div className="chat-dialog-status error">
          {sendError}
        </div>
      )}

      <form className="chat-input" onSubmit={handleSendMessage}>
        <input
          type="text"
          value={messageText}
          onChange={(e) => setMessageText(e.target.value)}
          placeholder={isActive && hasJoined 
            ? "Type your message..." 
            : "Waiting for chat to be activated..."}
          disabled={sending || !isActive || !hasJoined}
        />
        <button 
          type="submit" 
          disabled={sending || !messageText.trim() || !isActive || !hasJoined}
        >
          {sending ? (
            <FontAwesomeIcon icon={faSpinner} className="fa-spin" />
          ) : (
            <FontAwesomeIcon icon={faPaperPlane} />
          )}
        </button>
      </form>
    </div>
  );
};

export default ChatDialog;
