import React, { useState, useContext } from 'react';
import { LocationContext } from '../context/LocationContext';
import api from '../utils/api';
import './LocationSelector.css';

// List of predefined locations
const PREDEFINED_LOCATIONS = [
  { id: 1, name: "New York City", latitude: 40.7128, longitude: -74.0060, description: "The Big Apple" },
  { id: 2, name: "London", latitude: 51.5074, longitude: -0.1278, description: "Capital of England" },
  { id: 3, name: "Tokyo", latitude: 35.6762, longitude: 139.6503, description: "Japan's capital" },
  { id: 4, name: "Paris", latitude: 48.8566, longitude: 2.3522, description: "City of Love" },
  { id: 5, name: "Sydney", latitude: -33.8688, longitude: 151.2093, description: "Australia's largest city" },
  { id: 6, name: "Dubai", latitude: 25.2048, longitude: 55.2708, description: "UAE's business hub" },
  { id: 7, name: "Mumbai", latitude: 19.0760, longitude: 72.8777, description: "India's financial center" },
  { id: 8, name: "Rio de Janeiro", latitude: -22.9068, longitude: -43.1729, description: "Brazil's iconic city" },
  { id: 9, name: "Cairo", latitude: 30.0444, longitude: 31.2357, description: "Egypt's capital" },
  { id: 10, name: "San Francisco", latitude: 37.7749, longitude: -122.4194, description: "Home of Silicon Valley" },
  { id: 11, name: "Berlin", latitude: 52.5200, longitude: 13.4050, description: "Germany's capital" },
  { id: 12, name: "Singapore", latitude: 1.3521, longitude: 103.8198, description: "The Lion City" }
];

const LocationSelector = () => {
  const { updateLocation, locationStatus, position } = useContext(LocationContext);
  const [selectedLocation, setSelectedLocation] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [isUpdating, setIsUpdating] = useState(false);
  const [message, setMessage] = useState(null);

  // Filter locations based on search term
  const filteredLocations = searchTerm 
    ? PREDEFINED_LOCATIONS.filter(loc => 
        loc.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
        loc.description.toLowerCase().includes(searchTerm.toLowerCase())
      )
    : PREDEFINED_LOCATIONS;

  const handleLocationSelect = (location) => {
    setSelectedLocation(location);
    setMessage(null);
  };

  const handleSubmit = async () => {
    if (!selectedLocation) {
      setMessage({ type: 'error', text: 'Please select a location' });
      return;
    }

    setIsUpdating(true);
    try {
      await api.post('/location/update', { 
        latitude: selectedLocation.latitude, 
        longitude: selectedLocation.longitude 
      });

      // Update context
      updateLocation(selectedLocation.latitude, selectedLocation.longitude);

      setMessage({ 
        type: 'success', 
        text: `Location updated to ${selectedLocation.name}` 
      });

      // Clear selected location
      setSelectedLocation(null);
    } catch (error) {
      console.error('Failed to update location:', error);
      setMessage({ 
        type: 'error', 
        text: 'Failed to update location. Please try again.' 
      });
    } finally {
      setIsUpdating(false);
    }
  };

  return (
    <div className="location-selector">
      <div className="location-selector-header">
        <h3>Select Your Location</h3>
        <p>Choose a location from the list below</p>
      </div>

      {message && (
        <div className={`message ${message.type}`}>
          {message.text}
        </div>
      )}

      <div className="current-location-status">
        <h4>Current Location</h4>
        {position ? (
          <div className="current-location-info">
            <p>
              Latitude: {position.latitude.toFixed(4)}, 
              Longitude: {position.longitude.toFixed(4)}
            </p>
            <p className="location-timestamp">
              Last updated: {locationStatus.lastUpdated 
                ? new Date(locationStatus.lastUpdated).toLocaleString() 
                : 'Never'}
            </p>
          </div>
        ) : (
          <p className="no-location">No location set</p>
        )}
      </div>

      <div className="location-search">
        <input
          type="text"
          placeholder="Search locations..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          className="search-input"
        />
      </div>

      <div className="locations-list">
        {filteredLocations.map(location => (
          <div 
            key={location.id}
            className={`location-item ${selectedLocation?.id === location.id ? 'selected' : ''}`}
            onClick={() => handleLocationSelect(location)}
          >
            <h4>{location.name}</h4>
            <p>{location.description}</p>
            <div className="coordinates">
              {location.latitude.toFixed(4)}, {location.longitude.toFixed(4)}
            </div>
          </div>
        ))}
      </div>

      <div className="location-actions">
        <button 
          className="update-button"
          onClick={handleSubmit}
          disabled={!selectedLocation || isUpdating}
        >
          {isUpdating ? 'Updating...' : 'Update My Location'}
        </button>
      </div>
    </div>
  );
};

export default LocationSelector;
