import React, { useContext, useState } from 'react';
import { AuthContext } from '../context/AuthContext';
import { LocationContext } from '../context/LocationContext';
import LocationSelector from '../components/LocationSelector';
import './Profile.css';

const Profile = () => {
  const { user } = useContext(AuthContext);
  // Use optional chaining to prevent the destructuring error
  const { position, hasSelectedLocation } = useContext(LocationContext) || {};
  const [isEditing, setIsEditing] = useState(false);
  const [formData, setFormData] = useState({
    fullName: user?.full_name || '',
    email: user?.email || '',
    bio: user?.bio || ''
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prevState => ({
      ...prevState,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    // Profile update logic would go here
    setIsEditing(false);
  };

  return (
    <div className="profile-container">
      <div className="profile-header">
        <h1>My Profile</h1>
        <p>Manage your account settings and location</p>
      </div>

      <div className="profile-content">
        <div className="profile-section user-info">
          <div className="section-header">
            <h2>User Information</h2>
            {!isEditing && (
              <button 
                className="edit-button"
                onClick={() => setIsEditing(true)}
              >
                Edit Profile
              </button>
            )}
          </div>

          {isEditing ? (
            <form onSubmit={handleSubmit} className="profile-form">
              <div className="form-group">
                <label>Full Name</label>
                <input 
                  type="text"
                  name="fullName"
                  value={formData.fullName}
                  onChange={handleInputChange}
                />
              </div>

              <div className="form-group">
                <label>Email</label>
                <input 
                  type="email"
                  name="email"
                  value={formData.email}
                  onChange={handleInputChange}
                  disabled
                />
                <small>Email cannot be changed</small>
              </div>

              <div className="form-group">
                <label>Bio</label>
                <textarea 
                  name="bio"
                  value={formData.bio}
                  onChange={handleInputChange}
                  rows="4"
                ></textarea>
              </div>

              <div className="form-actions">
                <button type="submit" className="save-button">Save Changes</button>
                <button 
                  type="button" 
                  className="cancel-button"
                  onClick={() => setIsEditing(false)}
                >
                  Cancel
                </button>
              </div>
            </form>
          ) : (
            <div className="profile-details">
              <div className="profile-avatar">
                {user?.username?.charAt(0).toUpperCase() || 'U'}
              </div>

              <div className="profile-info">
                <h3>{user?.username || 'Username'}</h3>

                <div className="info-item">
                  <span className="info-label">Full Name:</span>
                  <span className="info-value">{user?.full_name || 'Not set'}</span>
                </div>

                <div className="info-item">
                  <span className="info-label">Email:</span>
                  <span className="info-value">{user?.email || 'Not available'}</span>
                </div>

                <div className="info-item">
                  <span className="info-label">Bio:</span>
                  <span className="info-value bio-text">{user?.bio || 'No bio available'}</span>
                </div>

                <div className="info-item">
                  <span className="info-label">Member Since:</span>
                  <span className="info-value">
                    {user?.created_at 
                      ? new Date(user.created_at).toLocaleDateString() 
                      : 'Unknown'}
                  </span>
                </div>
              </div>
            </div>
          )}
        </div>

        <div className="profile-section location-section">
          <div className="section-header">
            <h2>Location Settings</h2>
          </div>

          <div className="location-status">
            <h3>Current Location Status</h3>

            {position ? (
              <div className="current-location">
                <div className="location-indicator active">
                  <span className="indicator-dot"></span>
                  <span>Location is set</span>
                </div>
                <p className="coordinates">
                  Latitude: {position.latitude?.toFixed(4)}, 
                  Longitude: {position.longitude?.toFixed(4)}
                </p>
                <p className="last-updated">
                  Last updated: {position.lastUpdated 
                    ? new Date(position.lastUpdated).toLocaleString() 
                    : 'Unknown'}
                </p>
              </div>
            ) : (
              <div className="no-location">
                <div className="location-indicator inactive">
                  <span className="indicator-dot"></span>
                  <span>No location set</span>
                </div>
                <p>You haven't selected a location yet. Use the selector below to choose your location.</p>
              </div>
            )}
          </div>

          <LocationSelector />
        </div>
      </div>
    </div>
  );
};

export default Profile;
