import React, { createContext, useState, useEffect, useContext } from 'react';
import api from '../utils/api';
import { AuthContext } from './AuthContext';

export const LocationContext = createContext();

export const LocationProvider = ({ children }) => {
  const { isAuthenticated, user } = useContext(AuthContext);
  const [locationStatus, setLocationStatus] = useState({
    permission: 'unknown',  // unknown, granted, denied, unavailable
    tracking: false,
    lastUpdated: null,
    error: null,
    isManuallySet: false,  // Add this flag to indicate manual location selection
  });
  const [position, setPosition] = useState(null);
  const [liveUsers, setLiveUsers] = useState([]);
  const [loadingUsers, setLoadingUsers] = useState(false);
  const [locationTrackingId, setLocationTrackingId] = useState(null);

  // Function to handle geolocation errors
  const handleGeolocationError = (error) => {
    let errorMessage;
    switch (error.code) {
      case error.PERMISSION_DENIED:
        errorMessage = "Location permission denied";
        setLocationStatus(prev => ({
          ...prev,
          permission: 'denied',
          error: errorMessage
        }));
        break;
      case error.POSITION_UNAVAILABLE:
        errorMessage = "Location information is unavailable";
        setLocationStatus(prev => ({
          ...prev,
          permission: 'unavailable',
          error: errorMessage
        }));
        break;
      case error.TIMEOUT:
        errorMessage = "Location request timed out";
        setLocationStatus(prev => ({
          ...prev,
          error: errorMessage
        }));
        break;
      default:
        errorMessage = "An unknown error occurred while getting location";
        setLocationStatus(prev => ({
          ...prev,
          error: errorMessage
        }));
        break;
    }
    console.warn(errorMessage);
  };

  // Function to manually update user's location
  const updateLocation = async (latitude, longitude) => {
    if (!isAuthenticated) return;

    try {
      const response = await api.post('/location/update', { latitude, longitude });

      // Update position state
      setPosition({ latitude, longitude });

      setLocationStatus(prev => ({
        ...prev,
        lastUpdated: new Date(),
        isManuallySet: true,
        error: null
      }));

      return response.data;
    } catch (error) {
      console.error('Failed to update location on server:', error);
      setLocationStatus(prev => ({
        ...prev,
        error: 'Failed to update location on server'
      }));
      return null;
    }
  };

  // Function to get current position and update server
  const getCurrentPosition = () => {
    if (!navigator.geolocation) {
      setLocationStatus(prev => ({
        ...prev,
        permission: 'unavailable',
        error: 'Geolocation is not supported by this browser'
      }));
      return;
    }

    navigator.geolocation.getCurrentPosition(
      async (position) => {
        const { latitude, longitude } = position.coords;
        setPosition({ latitude, longitude });
        await api.post('/location/update', { latitude, longitude });

        setLocationStatus(prev => ({
          ...prev, 
          permission: 'granted',
          tracking: true,
          lastUpdated: new Date(),
          isManuallySet: false,
          error: null
        }));
      },
      handleGeolocationError,
      { enableHighAccuracy: true, timeout: 10000, maximumAge: 0 }
    );
  };

  // Start tracking location
  const startLocationTracking = () => {
    if (!isAuthenticated) return;

    // Update status to online first
    updateOnlineStatus(true);

    // Only start automatic tracking if location is not manually set
    if (!locationStatus.isManuallySet) {
      // Get position immediately
      getCurrentPosition();

      // Then set up interval for tracking
      const intervalId = setInterval(() => {
        if (!locationStatus.isManuallySet) {
          getCurrentPosition();
        }
      }, 60000); // Update every minute

      setLocationTrackingId(intervalId);
    }

    return () => {
      if (locationTrackingId) {
        clearInterval(locationTrackingId);
      }
    };
  };

  // Stop tracking location
  const stopLocationTracking = () => {
    if (locationTrackingId) {
      clearInterval(locationTrackingId);
      setLocationTrackingId(null);

      // Update status to offline
      updateOnlineStatus(false);

      setLocationStatus(prev => ({
        ...prev,
        tracking: false
      }));
    }
  };

  // Update online status
  const updateOnlineStatus = async (isOnline) => {
    if (!isAuthenticated) return;

    try {
      await api.post('/location/status', { isOnline });
    } catch (error) {
      console.error('Failed to update online status:', error);
    }
  };

  // Fetch all live users
  const fetchLiveUsers = async () => {
    if (!isAuthenticated) return [];

    setLoadingUsers(true);
    try {
      const response = await api.get('/location/live');
      setLiveUsers(response.data.data || []);
      return response.data.data;
    } catch (error) {
      console.error('Failed to fetch live users:', error);
      return [];
    } finally {
      setLoadingUsers(false);
    }
  };

  // Start tracking when authenticated
  useEffect(() => {
    if (isAuthenticated) {
      startLocationTracking();
    } else {
      stopLocationTracking();
    }

    // Cleanup on unmount
    return () => {
      stopLocationTracking();
    };
  }, [isAuthenticated]);

  // Handle beforeunload event to update offline status
  useEffect(() => {
    const handleBeforeUnload = () => {
      updateOnlineStatus(false);
    };

    window.addEventListener('beforeunload', handleBeforeUnload);

    return () => {
      window.removeEventListener('beforeunload', handleBeforeUnload);
    };
  }, []);

  return (
    <LocationContext.Provider
      value={{
        locationStatus,
        position,
        liveUsers,
        loadingUsers,
        getCurrentPosition,
        updateLocation,
        startLocationTracking,
        stopLocationTracking,
        updateOnlineStatus,
        fetchLiveUsers
      }}
    >
      {children}
    </LocationContext.Provider>
  );
};
