import React, { useEffect, useState, useContext } from 'react';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import { LocationContext } from '../context/LocationContext';
import { AuthContext } from '../context/AuthContext';
import 'leaflet/dist/leaflet.css';
import './UserLocationMap.css';

// Fix for Leaflet marker icons in React
import L from 'leaflet';
import iconMarker from 'leaflet/dist/images/marker-icon.png';
import iconRetina from 'leaflet/dist/images/marker-icon-2x.png';
import iconShadow from 'leaflet/dist/images/marker-shadow.png';

// Fix default icon issue
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: iconRetina,
  iconUrl: iconMarker,
  shadowUrl: iconShadow
});

// Custom icons for different users
const currentUserIcon = new L.Icon({
  iconUrl: 'https://raw.githubusercontent.com/pointhi/leaflet-color-markers/master/img/marker-icon-2x-blue.png',
  shadowUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/0.7.7/images/marker-shadow.png',
  iconSize: [25, 41],
  iconAnchor: [12, 41],
  popupAnchor: [1, -34],
  shadowSize: [41, 41]
});

const otherUserIcon = new L.Icon({
  iconUrl: 'https://raw.githubusercontent.com/pointhi/leaflet-color-markers/master/img/marker-icon-2x-red.png',
  shadowUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/0.7.7/images/marker-shadow.png',
  iconSize: [25, 41],
  iconAnchor: [12, 41],
  popupAnchor: [1, -34],
  shadowSize: [41, 41]
});

const UserLocationMap = () => {
  const { liveUsers, fetchLiveUsers, position } = useContext(LocationContext);
  const { user } = useContext(AuthContext);
  const [mapCenter, setMapCenter] = useState([0, 0]);
  const [mapZoom, setMapZoom] = useState(2);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    const loadMapData = async () => {
      setIsLoading(true);
      await fetchLiveUsers();

      // If current user has location, center map there
      if (position) {
        setMapCenter([position.latitude, position.longitude]);
        setMapZoom(13);
      } 
      // Otherwise, if any user has location, use average position
      else if (liveUsers.some(user => user.latitude && user.longitude)) {
        const usersWithLocation = liveUsers.filter(user => user.latitude && user.longitude);
        const avgLat = usersWithLocation.reduce((sum, user) => sum + user.latitude, 0) / usersWithLocation.length;
        const avgLng = usersWithLocation.reduce((sum, user) => sum + user.longitude, 0) / usersWithLocation.length;
        setMapCenter([avgLat, avgLng]);
        setMapZoom(10);
      }

      setIsLoading(false);
    };

    loadMapData();

    // Refresh map data every 20 seconds
    const intervalId = setInterval(fetchLiveUsers, 20000);

    return () => clearInterval(intervalId);
  }, []);

  // Update map when position changes
  useEffect(() => {
    if (position) {
      setMapCenter([position.latitude, position.longitude]);
      if (mapZoom < 10) setMapZoom(13);
    }
  }, [position]);

  const formatLastActive = (timestamp) => {
    if (!timestamp) return 'Unknown';

    const date = new Date(timestamp);
    const now = new Date();
    const diffSeconds = Math.floor((now - date) / 1000);

    if (diffSeconds < 60) {
      return 'Just now';
    } else if (diffSeconds < 3600) {
      const minutes = Math.floor(diffSeconds / 60);
      return `${minutes} minute${minutes > 1 ? 's' : ''} ago`;
    } else {
      return date.toLocaleTimeString();
    }
  };

  const getCurrentDateTime = () => {
    const now = new Date();
    return now.toLocaleString();
  };

  if (isLoading) {
    return <div className="map-loading">Loading map...</div>;
  }

  const usersWithLocation = liveUsers.filter(u => u.latitude && u.longitude);

  if (usersWithLocation.length === 0) {
    return (
      <div className="no-location-data">
        <h3>No Location Data Available</h3>
        <p>None of the active users have shared their location.</p>
      </div>
    );
  }

  return (
    <div className="map-container">
      <div className="map-header">
        <h3>Live User Map</h3>
        <div className="map-stats">
          <span>{usersWithLocation.length} users with location</span>
          <span>Last updated: {getCurrentDateTime()}</span>
        </div>
      </div>

      <MapContainer 
        center={mapCenter} 
        zoom={mapZoom} 
        style={{ height: '500px', width: '100%' }}
      >
        <TileLayer
          attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
        />

        {usersWithLocation.map(u => (
          <Marker 
            key={u.id} 
            position={[u.latitude, u.longitude]}
            icon={u.id === user?.id ? currentUserIcon : otherUserIcon}
          >
            <Popup>
              <div className="map-popup">
                <strong>{u.username}</strong>
                <div className="popup-status">
                  <span className="popup-status-dot"></span>
                  Online
                </div>
                <div className="popup-timestamp">
                  Active: {formatLastActive(u.last_active)}
                </div>
                {u.id === user?.id && (
                  <div className="popup-current-user">
                    This is you
                  </div>
                )}
              </div>
            </Popup>
          </Marker>
        ))}
      </MapContainer>

      <div className="map-legend">
        <div className="legend-item">
          <div className="legend-marker current-user"></div>
          <span>Your location</span>
        </div>
        <div className="legend-item">
          <div className="legend-marker other-user"></div>
          <span>Other users</span>
        </div>
      </div>
    </div>
  );
};

export default UserLocationMap;
