import React, { createContext, useState, useEffect } from 'react';
import api from '../utils/api';

// Create the context
export const AuthContext = createContext(null);

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [loading, setLoading] = useState(true);

  // Check if user is logged in on component mount
  useEffect(() => {
    const checkLoggedIn = async () => {
      // Check if token exists in localStorage
      const token = localStorage.getItem('token');

      if (token) {
        try {
          // Fetch the user profile with the token
          const response = await api.get('/auth/profile');
          setUser(response.data);
          setIsAuthenticated(true);
        } catch (error) {
          console.error('Error validating token:', error);
          // Invalid token, clear it
          localStorage.removeItem('token');
          setIsAuthenticated(false);
          setUser(null);
        }
      }

      setLoading(false);
    };

    checkLoggedIn();
  }, []);

  // Login function - mapping email to username for the API
  const login = async (email, password) => {
    try {
      // Here's the key change: the API expects "username", not "email"
      const response = await api.post('/auth/login', { 
        username: email, // Map email input to username field
        password 
      });

      const { token, user } = response.data;

      // Save token to localStorage
      localStorage.setItem('token', token);

      // Update state
      setUser(user);
      setIsAuthenticated(true);

      return { success: true };
    } catch (error) {
      console.error('Login error:', error);
      return { 
        success: false, 
        message: error.response?.data?.message || 'Login failed. Please try again.' 
      };
    }
  };

  // Register function might also need similar adjustment
  const register = async (userData) => {
    try {
      // If backend expects username but frontend collects email
      const response = await api.post('/auth/register', {
        ...userData,
        username: userData.email // Map email to username if needed
      });
      return { success: true, data: response.data };
    } catch (error) {
      console.error('Registration error:', error);
      return { 
        success: false, 
        message: error.response?.data?.message || 'Registration failed. Please try again.' 
      };
    }
  };

  // Logout function
  const logout = () => {
    // Remove token from localStorage
    localStorage.removeItem('token');

    // Update state
    setUser(null);
    setIsAuthenticated(false);
  };

  return (
    <AuthContext.Provider
      value={{
        isAuthenticated,
        user,
        loading,
        login,
        register,
        logout
      }}
    >
      {children}
    </AuthContext.Provider>
  );
};
